import { NavLink, Outlet, ScrollRestoration } from "react-router-dom";
import logo from "../images/logo.svg";

const RootLayout = () => {
  return (
    <div className="rootlayout">
      <ScrollRestoration />
      <header>
        <nav>
          <div className="logo">
            <NavLink to="/">
              <img src={logo} alt="logo" width={"120"} />
            </NavLink>
          </div>
          <div className="flexbox">
            <h2>EN</h2>
            <h2>P</h2>
          </div>
        </nav>
      </header>
      <main>
        <Outlet />
      </main>
    </div>
  );
};

export default RootLayout;
