import { buildStyles, CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import { Link } from "react-router-dom";

const MoviesCard = ({ movie }) => {
  const percentage = movie.vote_average * 10;
  const styles = buildStyles({
    // text size
    textSize: '16px',

    // animation duration in seconds
    pathTransitionDuration: 0.7,

    // colors
    pathColor:'',
    textColor: '',
    trailColor: '',
    backgroundColor:'#000'
  });
  switch (percentage) {
    case percentage <= 25:
      styles.
      break;
    case percentage > 25 && percentage < 50:

      break;
    case percentage >= 50 && percentage < 75:
      break;

    case percentage >= 75 && percentage < 100:
      break;
    default:
      break;
  }

  return (
    <div className="movie-card" key={movie.id}>
      <div className="image">
        <img
          src={`https://image.tmdb.org/t/p/w200/${movie.poster_path}`}
          alt={movie.title}
        />
        <div className="options">
          <div className="glyphs dots"></div>
          <div className="dropped-options">
            <div className="group">
              <span className="glyphs save"></span>
              <button className="watch-btn">Watch Later</button>
            </div>
            <hr />
            <div className="group">
              <span className="glyphs heart"></span>
              <button className="fav-btn">Favorite</button>
            </div>
          </div>
        </div>
        <div className="progress-bar">
          <CircularProgressbar
            value={percentage}
            text={percentage.toString() + "%"}
            background={true}
          />
        </div>
      </div>
      <Link to={`movies/${movie.id}`}>
        <h3 className="movie-title">{movie.title}</h3>
      </Link>
      <p className="movie-summary">
        {movie.overview.slice(0, 40).padEnd(45, ".")}
      </p>
    </div>
  );
};

export default MoviesCard;
