import { useActionData, useLoaderData, useParams } from "react-router-dom";
import FilterSearch from "../../components/FilterSearch";
import MoviePanel from "../../components/MoviePanel";

const MoviesList = () => {
  const movies = useLoaderData().results;
  console.log(useParams());

  const error =
    movies.length === 0 ? "No Movies were found, try searching again" : null;

  return (
    <>
      <FilterSearch />
      <div className="movies-page">
        {error && <div className="movie-error">{error}</div>}
        {movies && (
          <div className="movie-grid">
            {movies &&
              movies.map((movie) => (
                <MoviePanel movie={movie} key={movie.id} />
              ))}
          </div>
        )}
      </div>
      <div className="page-container">
        <div className="back-btn">
          <button>Back</button>
        </div>
        <div className="page-number"></div>
        <div className="forward-btn">
          <button>Forward</button>
        </div>
      </div>
    </>
  );
};

export default MoviesList;

export const moviesListLoader = async ({ request }) => {
  const url = new URL(request.url);
  const searchTerm = url.searchParams.get("search");
  const response = await fetch(`
  https://api.themoviedb.org/3/search/movie?api_key=0557b758465b10519557edb25fc53d86&language=en-US&query=${searchTerm}`);
  return response;
};
