import { useEffect, useState } from "react";
import {
  Link,
  useActionData,
  useLoaderData,
  useSearchParams,
} from "react-router-dom";
import MoviePanel from "../../components/MoviePanel";
import SearchForm from "../../components/SearchForm";
import SearchFetch from "../../Fetch/SearchFetch";
import { api_key, url } from "../../Global";

const MoviesList = () => {
  // const movies = useLoaderData().results;
  // const error =
  //   movies.length === 0 ? "No Movies were found, try searching again" : null;
  const [page, setPage] = useState(1);
  const [searchParams] = useSearchParams();
  const search = searchParams.get("search");
  const genre = searchParams.get("genre");
  const year = searchParams.get("year");
  const rating = searchParams.get("rating");
  const { data, isLoading, error } = SearchFetch(
    `${url}/search/movie?api_key=${api_key}&query=${search}&page=${page}&year=${year}`
  );
  const movies = data ? data.results : [];
  if (genre !== null) {
    movies.filter((movie) => {
      return movie.genre_ids.map((genreId) => {
        return genreId === parseInt(genre) ? true : false;
      });
    });
  }
  if (rating !== "") {
    movies.filter((movie) => {
      return movie.vote_average === (rating) ? true : false;
    });
  }
  console.log(movies);

